import React, { Component } from "react";
import CVInfo from "./CVInfo";

class PersonalInformation extends Component {
    constructor(props) {
        super(props);

        this.state = {
            info: {
                firstName: "",
                lastName: "",
                email: "",
                phone: ""
            }
        }
    }

    static onSubmitInfo = (e) => {
        let form = document.forms[0];
        e.preventDefault();
        this.setState({
            info:{
                firstName: form[0].value,
                lastName: form[1].value,
                email: form[2].value,
                phone: form[3].value
            }
        })
        CVInfo.formSubmitted(form);
    }

    render() {
        return (
            <div className = "p-info">
                <h2 className="info-header">Personal Information</h2>
                <form id = "info-form" >
                    <div className="input-fields">
                        <input placeHolder = "First Name" id="first-name" type="text" required></input>
                        <input placeHolder = "Last Name" id="last-name" type="text" required></input>
                        <input placeHolder = "Email"id="email" type="email" required></input>
                        <input placeHolder = "Phone" id="Phone" type="text" required></input>
                        <input type = "submit"></input>
                    </div>
                </form>
            </div>

        );
    }
}
export default PersonalInformation;
