import React, { Component } from "react";

class CVInfo extends Component {
    constructor(props) {
        super(props);

        this.state = {
            info: {
                firstName: "",
                lastName: "",
                email: "",
                phone: "",
            }
        }
    }
    formSubmitted = (form) =>{
        this.setState({
            info:{
                firstName: form[0].value,
                lastName: form[1].value,
                email: form[2].value,
                phone: form[3].value
            },
        });
    }

    render() {
        const {info} = this.state;
        return (
            <div className = "cv-p-info">
                <div className="cv-pinfo">
                    <div>
                        <h3>{info.info.firstName + " " + info.info.lastName}</h3>
                    </div>
                    <div>
                        <p>{info.email}</p>
                    </div>
                    <div>
                        <p>{info.phone}</p>
                    </div>
                </div>
                <div className = "cv-einfo">
                    <div>
                        <h3>University Name</h3>
                    </div>
                    <div>
                        <p>City</p>
                    </div>
                    <div>
                        <p>Degree</p>
                    </div>
                    <div>
                        <p>Graduation Year</p>
                    </div>
                </div>
            </div>

        );
    }
}
export default CVInfo
