import React, { Component } from "react";



class PersonalInformation extends Component {
    constructor(props) {
        super(props);

        this.state = {
            info: {
                firstName: "",
                lastName: "",
                email: "",
                phone: ""
            }
        }
    }
    onChange = (e) =>{
        this.setState({
            info:{
                firstName: e.value,
                lastName: e.value,
                email: e.value,
                phone: e.value
            }
        })
    }
    onSubmitInfo = (e) => {
        e.preventDefault();
        let form = document.forms[0];
       
        this.setState({
            info:{
                firstName: form[0].value,
                lastName: form[1].value,
                email: form[2].value,
                phone: form[3].value
            }
        });
        this.props.submission(form);
    }
    

    render() {
        return (
            <div className = "p-info">
                <h2 className="info-header">Personal Information</h2>
                <form onSubmit={this.onSubmitInfo} id = "info-form" >
                    <div className="input-fields">
                        <input onChage = {this.onChange} placeHolder = "First Name" id="first-name" type="text" required></input>
                        <input onChage = {this.onChange} placeHolder = "Last Name" id="last-name" type="text" required></input>
                        <input onChage = {this.onChange} placeHolder = "Email"id="email" type="email" required></input>
                        <input onChage = {this.onChange} placeHolder = "Phone" id="Phone" type="text" required></input>
                        <input type = "submit"></input>
                    </div>
                </form>
                
            </div>

        );
    }
}
export default PersonalInformation;
